import java.util.Random;

public class RetryTask implements Runnable {
    private static final int MAX_RETRY_TIMES = 5;
    private Random random = new Random();

    @Override
    public void run() {
        boolean success = false;
        int retryTimes = 0;
        while (!success && retryTimes < MAX_RETRY_TIMES) {
            try {
                success = doTask();
            } catch (Exception e) {
                retryTimes++;
                System.out.println("Task failed, retrying..." + retryTimes);
            }
        }
        if (success) {
            System.out.println("Task finished successfully.");
        } else {
            System.out.println("Task failed after " + MAX_RETRY_TIMES + " attempts.");
        }
    }

    // This is the task that has a 20% chance to fail
    private boolean doTask() throws Exception {
        int n = random.nextInt(5);
        if (n == 0) {
            throw new Exception("Task failed");
        }
        return true;
    }

    public static void main(String[] args) {
        RetryTask task = new RetryTask();
        Thread thread = new Thread(task);
        thread.start();
    }
}

import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.scheduling.concurrent.ThreadPoolTaskExecutor;

import java.util.concurrent.ThreadPoolExecutor;

@Component
public class TaskManager {

    @Autowired
    private RedisTemplate<String, Runnable> redisTemplate;

    private ThreadPoolTaskExecutor executor;

    public TaskManager() {
        executor = new ThreadPoolTaskExecutor();
        executor.setCorePoolSize(5);
        executor.setMaxPoolSize(10);
        executor.setQueueCapacity(100);
        executor.setRejectedExecutionHandler(new ThreadPoolExecutor.CallerRunsPolicy());
        executor.initialize();
    }

    public void submitTask(Runnable task) {
        // Save task to Redis before submitting
        redisTemplate.opsForList().leftPush("taskQueue", task);
        executor.execute(() -> {
            task.run();
            // Remove task from Redis after successful execution
            redisTemplate.opsForList().remove("taskQueue", 0, task);
        });
    }

    public void recoverTasks() {
        // Fetch tasks from Redis and submit them for execution
        Long size = redisTemplate.opsForList().size("taskQueue");
        for (int i = 0; i < size; i++) {
            Runnable task = redisTemplate.opsForList().rightPop("taskQueue");
            if (task != null) {
                submitTask(task);
            }
        }
    }
}

