user nginx;
worker_processes auto;
pid /run/nginx.pid;

events {
    worker_connections 1024;
}

http {

		#æ—¥å¿—æ ¼å¼
   log_format  main  '$remote_addr - $remote_user [$time_local] "$request" '
                      '$status $body_bytes_sent "$http_referer" '
                      '"$http_user_agent" "$http_x_forwarded_for"';
	  #æ—¥å¿—æ–‡ä»¶
		error_log /var/log/nginx/error.log main;
		access_log  /var/log/nginx/access.log main;

    sendfile            on;
    tcp_nopush          on;
    keepalive_timeout   65;
    types_hash_max_size 4096;
    include             /etc/nginx/mime.types;
    default_type        application/octet-stream;

    #confæ–‡ä»¶åˆ†å±‚
    include /etc/nginx/conf.d/*.conf;

    #ç¦æ­¢é€šè¿‡ipç›´æ¥è®¿é—®80ç«¯å£
    server {
        listen 80  default_server;
        server_name _;
        return 444;
    }

}

server {
        listen       80;
        server_name  password.com;

        location / {
            #basicå¯†ç è®¿é—®
            auth_basic "Restricted Access";
            auth_basic_user_file /etc/nginx/access_pwd;
            proxy_pass http://192.168.12.34:1234/;
            proxy_set_header Host $host:$server_port;
        }

}

server {
        listen       80;
        server_name  ip.com;
        location / {
		        #åªå…è®¸ç‰¹å®šipæ®µè®¿é—®
            allow 192.168.12.34/24;
            deny all;
            proxy_pass http://192.168.12.34:9876/;
            proxy_set_header Host $host:$server_port;
        }
}

server {
        listen       443;
        server_name  ssl.cn;

        ssl_certificate      /etc/nginx/cert/_.x.pem;
        ssl_certificate_key  /etc/nginx/cert/_.x.key;

        ssl_session_cache    shared:SSL:1m;
        ssl_session_timeout  5m;

        ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:ECDHE:ECDH:AES:HIGH:!NULL:!aNULL:!MD5:!ADH:!RC4;
        ssl_protocols TLSv1 TLSv1.1 TLSv1.2;

        ssl_prefer_server_ciphers  on;

        location / {
            proxy_pass http://192.168.0.1:7654/;
            proxy_set_header Host $host:$server_port;
        }
}

